<template>
  <div class="main">
    <v-btn small outlined @click="printPrescription()">Print</v-btn>
    <v-card>
      <v-data-table
        :items="prescriptionList"
        :headers="headers"
        :search="search"
        :items-per-page="10"
      >
        <template v-slot:item.action>
          <Detail class="icon" @click="detailPrescription(item.id)" />
        </template>
      </v-data-table>
    </v-card>
  </div>
</template>

<script>
import Detail from "@/assets/icons/eye.svg";
import { mapActions, mapState } from "vuex";
import { jsPDF } from "jspdf";
import "jspdf-autotable";

export default {
  data() {
    return {
      search: "",
      headers: [
        { text: "Full Name", value: "fullName" },
        { text: "Card Number", value: "card_number" },
        { text: "From", value: "ordered_by" },
        { text: "Status", value: "status" },
        { text: "Action", value: "action" },
      ],
      medicineList: [
        {
          medicine: "Parastmonl",
          dosage: "2",
          totalQTY: "30",
          duration: "4hrs",
          route: "Mouth",
          frequency: "3 in a day",
        },
      ],

      logo: "data:image/jpeg;base64,",
    };
  },

  components: {
    Detail,
  },

  created() {
    this.loadData();
  },

  computed: {
    ...mapState("prescription", ["prescriptionList"]),
  },

  methods: {
    ...mapActions("prescription", ["getPrescriptionList"]),

    async loadData() {
      await this.getPrescriptionList();
    },

    async detailPrescription(id) {
      this.$router.push({
        name: "prescriptionDetail",
        params: { prescriptionId: id },
      });
    },

    addHeaderFooter(doc, x, y, minspa, minimspa, tab, xin) {
      doc.addImage(this.logo, "JPEG", x + 15, y - 0.5, 2.5, 2.5);

      doc.setFont("Georgia", "bold");
      doc.setFontSize(17);
      doc.setTextColor(0, 0, 0);
      doc.text("Hospital Name", x, y);
      doc.setFont();

      //Gray
      doc.setTextColor(100, 100, 100);
      doc.setFontSize(8);
      y = y + minimspa;
      y = y + minimspa;
      x = x + tab;
      doc.text(
        "Empowering People to Improve Their Lives. **** Hospital. ",
        x,
        y
      );

      doc.setFont();
      doc.setFontSize(11);
      y = y + minspa;
      doc.text("Location : Bahir Dar", x, y);
      y = y + minimspa;
      y = y + minimspa;
      doc.text("Contact : 058226....", x, y);
      y = y + minimspa;
      y = y + minimspa;
      doc.text("Fax : 789", x, y);
      y = y + minimspa;
      y = y + minimspa;
      doc.text("Email : hospital@gmail.com", x, y);

      let fy = 27.5;
      x = xin;
      x = x + 3;
      doc.text(
        "If you have any questions concerning this prescription don't' hesitate to cotact us!!!",
        x,
        fy
      );

      fy = fy + minspa;
      x = x + 2.5;
      doc.setFont("Georgia", "bold");
      doc.setFontSize(10);
      doc.text("THANK YOU FOR YOUR HOSPITALITY!!!", x, fy);

      return { x, y };
    },

    async printPrescription() {
      let xin = 2;
      let yin = 2;
      let tab = 0.5;
      let minspa = 0.7;
      let minimspa = 0.2;
      let maxspa = 1;

      let x = xin;
      let y = yin;

      const doc = new jsPDF({
        orientation: "portrait",
        unit: "cm",
        format: "a4",
      });

      let xy = this.addHeaderFooter(doc, x, y, minspa, minimspa, tab, xin);
      x = xy.x;
      y = xy.y;

      x = xin;
      y = y + minspa;
      y = y + minspa;

      doc.setFont("Georgia", "bold");
      doc.setFontSize(15);
      doc.setTextColor(0, 0, 0);
      doc.text("Prescription", x, y);

      y = y + maxspa;
      doc.setFontSize(12);
      doc.setTextColor(100, 100, 100);
      doc.setFont();
      doc.text("Patient Name : Temesgen Kefie", x, y);

      y = y + minspa;
      doc.text("Date Of Birth : 04-06-1998", x, y);

      y = y + minspa;
      y = y + minspa;
      doc.text("Date Make : 19-09-2021", x, y);

      y = y + minspa;
      y = y + minspa;
      doc.text("Name Of Medicine and Description :", x, y);
      x = x + tab;
      x = x + tab;

      y = y + 1;
      x = xin;
      //Amber color
      doc.setTextColor(250, 100, 0);
      doc.setFontSize(12);
      doc.text("Medicine List and Detail Specification", x, y);
      let col = [
        "Medicine",
        "Dosage",
        "Total Quantity",
        "Duration",
        "Route",
        "Frequency",
      ];
      let data = [];
      for (let i = 0; i < this.medicineList.length; i++) {
        data.push([
          this.medicineList[i].medicine,
          this.medicineList[i].dosage,
          this.medicineList[i].totalQTY,
          this.medicineList[i].duration,
          this.medicineList[i].route,
          this.medicineList[i].frequency,
        ]);
      }
      y = y + minimspa;
      doc.autoTable(col, data, {
        headerStyles: {
          halign: "center",
        },
        styles: {
          fontSize: 8,
          halign: "center",
        },
        startY: y,
        margin: { left: 2 },
      });

      doc.save("prescription.pdf");
    },
  },
};
</script>

<style scoped>
.main {
  margin: 5%;
  margin-top: 2%;
}

.icon {
  cursor: pointer;
}
</style>
